import React, { useEffect } from 'react'
import { Disclosure } from '@headlessui/react'
import { Link } from 'react-router-dom'
import { useLocation } from 'react-router-dom'
import bunderraLogo from './images/bunderraLogoInternal.svg'
import burger from './images/burger.svg'

export default function Header() {
  const [navbarOpen, setNavbarOpen] = React.useState(false)
  const location = useLocation()

  //destructuring pathname from location
  const { pathname } = location

  // Update window value to correctly show the navigation
  const [windowWidth, setWidth] = React.useState(window.innerWidth)

  function detectWindowSize(e) {
    setWidth(window.innerWidth)
  }
  useEffect(() => {
    window.addEventListener('resize', detectWindowSize)
    return () => {
      window.removeEventListener('resize', detectWindowSize)
    }
  }, [])

  //Javascript split method to get the name of the path in array
  const splitLocation = pathname.split('/')
  return (
    <Disclosure as='nav' className='shadow' style={{ background: '#EDEDED' }}>
      <>
        <nav
          className='relative flex flex-wrap items-center justify-between headerTopNav fixedHeader'
          style={{ background: '#EDEDED' }}
        >
          <div className='container mx-auto flex flex-wrap items-center justify-between wrap'>
            {/* <div className='container px-16 mx-auto flex flex-wrap items-center justify-between'> */}
            <div className='w-full relative flex justify-between py-2 lg:w-auto lg:static lg:block lg:justify-start navMobTitleBox'>
              <Link
                className='text-sm font-bold leading-relaxed inline-block mr-4 whitespace-nowrap uppercase text-white'
                to='/'
              >
                {/* <img
                  src={bunderraLogo}
                  className='bunderraLogo'
                  style={{ paddingTop: '18px' }}
                /> */}
                <img
                  className={`bunderraLogo ${
                    windowWidth <= 480 ? ' mobileLogoImg' : ''
                  }`}
                  src={bunderraLogo}
                  // style={{ paddingTop: '18px' }}
                  alt='Bunderra Logo'
                />
              </Link>
              <button
                className={`text-white cursor-pointer text-xl leading-none px-3 py-1 border border-solid border-transparent rounded bg-transparent block lg:hidden outline-none focus:outline-none ${
                  windowWidth < 1253 ? ' burgerShow' : ' burgerHidden'
                }`}
                // style={{ paddingTop: '18px' }}
                type='button'
                onClick={() => setNavbarOpen(!navbarOpen)}
              >
                <img src={burger} alt='menu burger icon' />
                {/* <svg
                  aria-hidden='true'
                  // style={{ width: '12%', float: 'right' }}
                  focusable='false'
                  data-prefix='fas'
                  data-icon='bars'
                  class='svg-inline--fa fa-bars fa-w-14 menuIcon'
                  role='img'
                  xmlns='http://www.w3.org/2000/svg'
                  viewBox='0 0 448 512'
                >
                  <path
                    fill='#fff'
                    d='M16 132h416c8.837 0 16-7.163 16-16V76c0-8.837-7.163-16-16-16H16C7.163 60 0 67.163 0 76v40c0 8.837 7.163 16 16 16zm0 160h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16zm0 160h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16z'
                  ></path>
                </svg> */}
              </button>
            </div>
            <div
              className={
                'lg:flex flex-grow items-center relative heighMenu' +
                (navbarOpen || windowWidth > 1252 ? ' flex' : ' navHidden')
              }
              id='example-navbar-danger'
            >
              <ul
                className='flex flex-col lg:flex-row list-none lg:ml-auto headerNavListBox'
                // style={{ margin: 'auto' }}
              >
                <li className='nav-item'>
                  <Link
                    to='/aboutus'
                    className={`fontSz fontRobotoReg border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                      splitLocation[1] === 'aboutus' ? 'active' : ''
                    }`}
                    style={{ color: 'rgba(123, 143, 143,1)' }}
                  >
                    About us
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='/grazingproperties'
                    className={`fontSz fontRobotoReg border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                      splitLocation[1] === 'grazingproperties' &&
                      windowWidth > 1252
                        ? 'active'
                        : ''
                    }`}
                    style={{ color: 'rgba(123, 143, 143,1)' }}
                  >
                    Grazing properties
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='/organiccertifiedbeef'
                    className={`fontSz fontRobotoReg border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                      splitLocation[1] === 'organiccertifiedbeef' &&
                      windowWidth > 1252
                        ? 'active'
                        : ''
                    }`}
                    style={{ color: 'rgba(123, 143, 143,1)' }}
                  >
                    Organic Certified Beef
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='workforus'
                    className={` fontSz fontRobotoReg border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                      splitLocation[1] === 'workforus' && windowWidth > 1252
                        ? 'active'
                        : ''
                    }`}
                    style={{ color: 'rgba(123, 143, 143,1)' }}
                  >
                    Work for us
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='contactus'
                    className={`fontSz fontRobotoReg border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium ${
                      splitLocation[1] === 'contactus' && windowWidth > 1252
                        ? 'active'
                        : ''
                    }`}
                    style={{ color: 'rgba(123, 143, 143,1)' }}
                  >
                    Contact us
                  </Link>
                </li>
              </ul>
              <div className={`sm:ml-6 sm:items-center headerIconsBox`}>
                {/* className={`${
                navbarOpen ? ' flex' : ' hidden'
              } sm:ml-6 sm:flex sm:items-center navIconsBox`} */}
                <a
                  href='https://www.facebook.com/bunderracattleco'
                  target='_blank'
                  rel='noreferrer'
                >
                  <button
                    type='button'
                    style={{ color: '#EDEDED' }}
                    className='p-2 rounded-full text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 '
                  >
                    <svg
                      fill='currentColor'
                      // viewBox='0 0 24 24'
                      viewBox='0 0 25 25'
                      // class='h-6 w-6'
                      class='h-10 w-10'
                      aria-hidden='true'
                    >
                      <path
                        fill-rule='evenodd'
                        d='M22 12c0-5.523-4.477-10-10-10S2 6.477 2 12c0 4.991 3.657 9.128 8.438 9.878v-6.987h-2.54V12h2.54V9.797c0-2.506 1.492-3.89 3.777-3.89 1.094 0 2.238.195 2.238.195v2.46h-1.26c-1.243 0-1.63.771-1.63 1.562V12h2.773l-.443 2.89h-2.33v6.988C18.343 21.128 22 16.991 22 12z'
                        clip-rule='evenodd'
                      ></path>
                    </svg>
                  </button>
                </a>
                <a
                  href='https://www.instagram.com/bunderracattleco/?hl=en'
                  target='_blank'
                  rel='noreferrer'
                >
                  <button
                    type='button'
                    style={{ color: '#EDEDED' }}
                    className='p-2 rounded-full text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2'
                  >
                    <svg
                      fill='currentColor'
                      // viewBox='0 0 24 24'
                      viewBox='0 0 25 25'
                      // class='h-6 w-6'
                      class='h-10 w-10'
                      aria-hidden='true'
                    >
